import React from 'react';
import Accordion from 'react-bootstrap/Accordion';
import Card from 'react-bootstrap/Card';
import Button from 'react-bootstrap/Button';
import Row from 'react-bootstrap/Row';


class Alarm extends React.Component {

    static EventKey = 1;

    constructor(props) {
        super(props);

        this.state = {
            id: props.id,
            time: props.time,
            date: props.date,
            affectedSystems: props.affectedSystems,
            suspectedAttackType: props.suspectedAttackType,
            probability: props.probability,
            automaticReaction: props.automaticReaction,
            checklist: props.checklist,
            timePassed: "none",
            category: this.calculateCatergory(props.probability),
        };
        console.log(this.state.category[1]);
    }

    calculateCatergory(probability) {
        if (probability > 95) { return ["critical", { 'background-color': 'red' }] }
        else if (probability > 85) { return ["major", "orange"] }
        else { return ["minor", "yellow"] };
    }



    render() {
        return (
            <Card>

                <Card.Header>
                    <Accordion.Toggle as={Card.Header} class="p-0" eventKey={this.state.id}>
                        <Row>
                            <h5 class="font-weight-lighter ml-2 mt-n2">{this.state.id}</h5>
                        </Row>
                        <Row>
                            <h3 class="ml-2 mb-n1">{this.state.suspectedAttackType}</h3>
                            <div class="tag ml-2 mt-1" style={this.state.category[1]}>
                                &#8205; &#8205; {this.state.category[0]} &#8205; &#8205;
                            </div>
                            <div class="text-right w-100 mt-n4">
                                {this.state.timepassed}
                            </div>
                        </Row>
                    </Accordion.Toggle>
                </Card.Header>

                <Accordion.Collapse eventKey={this.props.id}>
                    <Card.Body>
                        {this.state.card_body}
                    </Card.Body>
                </Accordion.Collapse>
            </Card>);
    }
}

export default Alarm;
