import React from 'react';
import Accordion from 'react-bootstrap/Accordion';
import Card from 'react-bootstrap/Card';
import Button from 'react-bootstrap/Button';
import Row from 'react-bootstrap/Row';


class Alarm extends React.Component {

    static EventKey = 1;

    constructor(props) {
        super(props);
        this.state = {
            id: props.id,
            time: props.time,
            date: props.date,
            affectedSystems: props.affectedSystems,
            suspectedAttackType: props.suspectedAttackType,
            probability: props.probability,
            automaticReaction: props.automaticReaction,
            checklist: props.checklist,
            timePassed: None,
            category: this.calculateCatergory(props.probability),
        };
        console.log("test");

        this.calculateTag = this.calculateTag.bind(this);
    }

    calculateCatergory(probability) {
        if (probability > 95) { return "critical" }
        else if (probability > 85) { return "major" }
        else { return "minor" };
    }

    calculateTag(probality) {
        var tag = {
            color: "yellow",
            cat: "MINOR"
        }
        if (probality >= 75) {
            tag.color = "orange"
            tag.cat = "MAJOR"
        };
        if (probality >= 95) {
            tag.color = "#E94D4D"
            tag.cat = "CRITICAL"
        };
        return tag;
    }

    render() {
        this.calculateTimePassed();
        return (
            <Card>

                <Card.Header>
                    <Accordion.Toggle as={Card.Header} class="p-0" eventKey={this.props.id}>
                        <Row>
                            <h5 class="font-weight-lighter ml-2 mt-n2">{this.state.id}</h5>
                        </Row>
                        <Row>
                            <h3 class="ml-2 mb-n1">{this.state.card_head.alarm_cat}</h3>
                            <div class="tag ml-2 mt-1" style={{ 'background-color': this.calculateTag(96).color }}>
                                &#8205; &#8205; {this.calculateTag(96).cat} &#8205; &#8205;
                            </div>
                            <div class="text-right w-100 mt-n4">
                                {this.state.timepassed}
                            </div>
                        </Row>
                    </Accordion.Toggle>
                </Card.Header>

                <Accordion.Collapse eventKey={this.props.id}>
                    <Card.Body>
                        {this.state.card_body}
                    </Card.Body>
                </Accordion.Collapse>
            </Card>);
    }
}

export default Alarm;
