import React from 'react';
import CanvasJSChart from '../lib/canvasjs.react';

class Chart extends React.Component {

    render() {
        var datapoints = [];
        this.props.data.forEach(alarm => {
            datapoints.push({ x: alarm.date, y: alarm.probability, color: (alarm.probability >= 95 ? "#E94D4D" : (alarm.probability >= 85 ? "#FF994F" : (alarm.probability >= 75 ? "#EDC535" : "#4A73B0"))), toolTipContent: `${alarm.date}` });
        });
        const options = {
            animationEnabled: false,
            exportEnabled: true,
            theme: "light1",
            title: {
                text: "Today's Events/Alarms",
                fontFamily: "open sans"
            },
            axisX: {
                minimum: + new Date(new Date().getFullYear(), new Date().getMonth(), new Date().getDate()),
                maximum: + new Date(new Date().getFullYear(), new Date().getMonth(), new Date().getDate(), 23, 59, 59),
            },
            axisY: {
                title: "Probability",
                gridThickness: 0,
                includeZero: true,
                maximum: 100,
                stripLines: [
                    {
                        startValue: 75,
                        endValue: 75.5,
                        color: "#EDC535"
                    },
                    {
                        startValue: 85,
                        endValue: 85.5,
                        color: "#FF994F"
                    },
                    {
                        startValue: 95,
                        endValue: 95.5,
                        color: "#E94D4D"
                    },

                ]
            },
            dataPointMinWidth: 5,
            data: [{

                type: "scatter",
                xValueType: "dateTime",
                dataPoints: datapoints
            }]
        }

        return (
            <div>
                <CanvasJSChart options={options} />
            </div >
        );
    }
}

export default Chart;
