import React from 'react';
import Accordion from 'react-bootstrap/Accordion';
import Card from 'react-bootstrap/Card';
import Button from 'react-bootstrap/Button';
import Row from 'react-bootstrap/Row';


class Alarm extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            card_head: { alarm_id: "Alarm #0014", alarm_cat: "SQL-Injection", alarm_time: "20" },
            card_body: props.card_body,
            eventKey: props.eventKey
        };


        this.calculateTag = this.calculateTag.bind(this);
    }

    calculateTag(probality) {
        var tag = {
            color: "yellow",
            cat: "MINOR"
        }
        if (probality >= 75) {
            tag.color = "orange"
            tag.cat = "MAJOR"
        };
        if (probality >= 95) {
            tag.color = "#E94D4D"
            tag.cat = "CRITICAL"
        };
        return tag;
    }

    render() {
        return (
            <Card>

                <Card.Header>
                    <Accordion.Toggle as={Card.Header} class="p-0" eventKey={this.props.eventKey}>
                        <Row>
                            <h5 class="font-weight-lighter ml-2 mt-n2">{this.state.card_head.alarm_id}</h5>
                        </Row>
                        <Row>
                            <h3 class="ml-2 mb-n1">{this.state.card_head.alarm_cat}</h3>
                            <div class="tag ml-2 mt-4" style={{ 'background-color': this.calculateTag(96).color }}>
                                &#8205; &#8205; {this.calculateTag(96).cat} &#8205; &#8205;
                            </div>
                            <div id="time_ago">
                                {this.state.card_head.alarm_time} minutes ago
                            </div>
                        </Row>
                    </Accordion.Toggle>
                </Card.Header>

                <Accordion.Collapse eventKey={this.props.eventKey}>
                    <Card.Body>
                        {this.state.card_body}
                    </Card.Body>
                </Accordion.Collapse>
            </Card>);
    }
}

export default Alarm;
