import React from 'react';
import Accordion from 'react-bootstrap/Accordion';
import Card from 'react-bootstrap/Card';
import Button from 'react-bootstrap/Button';
import Row from 'react-bootstrap/Row';
import Form from 'react-bootstrap/Form'


class Alarm extends React.Component {

    constructor(props) {
        super(props);

        this.state = {
            id: props.id,
            time: props.time,
            date: props.date,
            affectedSystems: props.affectedSystems,
            suspectedAttackType: props.suspectedAttackType,
            probability: props.probability,
            automaticReaction: props.automaticReaction,
            checklist: props.checklist,
            category: this.calculateCatergory(props.probability)
        };
    }

    calculateTimePassed(time, date) {

    }

    calculateCatergory(probability) {
        if (probability > 95) { return ["critical", '#E94D4D'] }
        else if (probability > 85) { return ["major", '#FF994F'] }
        else { return ["minor", '#EDC535'] };
    }



    render() {
        return (
            <Card>

                <Card.Header>
                    <Accordion.Toggle as={Card.Header} class="p-0" eventKey={this.state.id}>
                        <Row>
                            <h5 class="font-weight-lighter ml-2 mt-n2">{this.state.id}</h5>
                        </Row>
                        <Row>
                            <h3 class="ml-2 mb-n1">{this.state.suspectedAttackType}</h3>
                            <div class="tag ml-2 mt-1" style={{ "background-color": this.state.category[1] }}>
                                &#8205; &#8205; {this.state.category[0]} &#8205; &#8205;
                            </div>
                            <div class="text-right w-100 mt-n4">
                                {this.state.date} {this.state.time}
                            </div>
                        </Row>
                    </Accordion.Toggle>
                </Card.Header>

                <Accordion.Collapse eventKey={this.props.id}>
                    <Card.Body>
                        <Form.Group controlId="formBasicCheckbox">
                            {this.state.checklist.map(check => {
                                <Form.Check>test</Form.Check>
                            })}
                            <Form.Check type="checkbox" label="Check me out" />
                        </Form.Group>
                    </Card.Body>
                </Accordion.Collapse>
            </Card>);
    }
}

export default Alarm;
