import React from 'react';
import Accordion from 'react-bootstrap/Accordion';
import Card from 'react-bootstrap/Card';
import Button from 'react-bootstrap/Button';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Container from 'react-bootstrap/Container';
import Alarm from './Alarm.js';
import { subscribeToEvent } from './api.js';

class App extends React.Component {

    constructor(props) {
        super(props);
        subscribeToEvent((err, EventData) => this.setState({
            EventData
        }));
    }
    state = {
        EventData: {}
    }

    render() {
        return (
            <Container fluid>
                {/* Header */}
                <Row>
                    <h1 class="ml-3 mt-1">Alarm Dashboard</h1>
                </Row>
                {/* Main Content */}
                <Row>
                    {/* Alarm List */}
                    <Col>
                        <Accordion defaultActiveKey="0">
                            <Alarm id="#0014" time="14:45:34" date="12.11.2020" affectedSystems="['server', 'testServer']"
                                suspectedAttackType="Brute Force" probability="93" automaticReaction="[]" checklist="[]"></Alarm>
                            <Alarm data={"#0015", "14:45:23", "12.11.2020", ['server', 'testServer'], "Denial of Service", 75, [], ['test', 'test2']}></Alarm>
                        </Accordion>
                    </Col>
                    {/* 24h-Course */}
                    <Col>24h-Course</Col>
                </Row>
            </Container >);
    }
}

export default App;
