/* App.js */
import React from 'react';
import CanvasJSChart from '../lib/canvasjs.react';

class Chart extends React.Component {

    constructor(props) {

        super(props);

        this.state = {
            alarmArray: props.data
        };
    }



    render() {
        var datapoints = [];
        this.state.alarmArray.forEach(alarm => {
            datapoints.push({ x: alarm.time, y: alarm.probability, indexLabel: "#" + alarm.id.toString() });
        });
        const options = {
            animationEnabled: true,
            exportEnabled: true,
            theme: "light1", //"light1", "dark1", "dark2"
            title: {
                text: "24h-Course"
            },
            axisY: {
                includeZero: true,
                title: "probability"
            },
            axisY: {
                includeZero: true,
                title: "time",
                stripLines: [
                    {
                        startValue: 75,
                        endValue: 85,
                        color: "#EDC535",
                        label: "Minor",
                        labelFontColor: "#a8a8a8",
                    },
                    {
                        startValue: 85,
                        endValue: 95,
                        color: "#FF994F",
                        label: "Major",
                        labelFontColor: "#a8a8a8",
                    },
                    {
                        startValue: 95,
                        endValue: 100,
                        color: "#E94D4D",
                        label: "Critical",
                        labelFontColor: "#a8a8a8",
                    },

                ]
            },
            data: [{
                type: "column", //change type to bar, line, area, pie, etc
                indexLabel: "{y}", //Shows y value on all Data Points
                indexLabelFontColor: "#5A5757",
                indexLabelPlacement: "outside",
                xValueType: "dateTime",
                dataPoints: datapoints
            }]
        }

        return (
            <div>
                <CanvasJSChart options={options} />
            </div>
        );
    }
}

export default Chart;
