import React from 'react';
import Accordion from 'react-bootstrap/Accordion';
import Card from 'react-bootstrap/Card';
import Button from 'react-bootstrap/Button';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';

class Alarm extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            card_head: { alarm_id: "Alarm #0014", alarm_cat: "SQL-Injection", alarm_time: "20" },
            card_body: props.card_body
        };
    }

    render() {
        return (
            <Card>
                <Card.Header>
                    <Accordion.Toggle as={Button} variant="" eventKey="0">
                        <Row class="mt-3 mr-0 mb-0 ml-3">
                            <h5 class="font-weight-lighter">{this.state.card_head.alarm_id}</h5>
                        </Row>
                        <Row class="mt-0 mr-0 mb-0 ml-3">
                            <h3 class="font-weight-bolder">{this.state.card_head.alarm_cat}</h3>
                        </Row>
                        <Row class="mt-0 mr-0 mb-0 ml-0">
                            <div class="tag" style={{ 'background-color': 'red' }}>
                                &#8205; &#8205; critical &#8205; &#8205;
                            </div>
                            <div id="time_ago">
                                {this.state.card_head.alarm_time} minutes ago
                            </div>


                        </Row>
                    </Accordion.Toggle>
                </Card.Header>
                <Accordion.Collapse eventKey="0">
                    <Card.Body>
                        {this.state.card_body}
                    </Card.Body>
                </Accordion.Collapse>
            </Card>);
    }
}

export default Alarm;
