import React from 'react';
import Accordion from 'react-bootstrap/Accordion';
import Card from 'react-bootstrap/Card';
import Button from 'react-bootstrap/Button';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Container from 'react-bootstrap/Container';
import Alarm from './Alarm.js';
import { subscribeToEvent } from './api.js';
import { generateRandomAlarm } from './generateAlarm.js';

class App extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            alarmArray: []
        }
        subscribeToEvent((err, EventData) => {
            this.state.alarmArray.unshift(EventData);
            this.state({});
        });
    }

    render() {
        return (
            <Container fluid>
                {/* Header */}
                <Row>
                    <h1 class="mx-auto mt-2">Alarm Dashboard</h1>
                </Row>
                {/* Main Content */}
                <Row>
                    {/* Alarm List */}
                    <Col>
                        <Accordion defaultActiveKey="#0017">
                            {this.state.alarmArray.map(alarm => {
                                return <Alarm data={alarm}></Alarm>
                            })}
                            <Alarm data={generateRandomAlarm()}></Alarm>
                            <Alarm data={generateRandomAlarm()}></Alarm>
                            <Alarm data={generateRandomAlarm()}></Alarm>
                            <Alarm data={generateRandomAlarm()}></Alarm>
                            <Alarm data={generateRandomAlarm()}></Alarm>
                            <Alarm data={generateRandomAlarm()}></Alarm>
                            <Alarm data={generateRandomAlarm()}></Alarm>
                            <Alarm data={{
                                id: "034017",
                                time: "14:45:23",
                                date: "12.11.2020",
                                affectedSystems: ['server'],
                                suspectedAttackType: "Man in the Middle",
                                probability: 89,
                                automaticReaction: [],
                                checklist: ['test', 'test2']
                            }}></Alarm>
                        </Accordion>
                    </Col>
                    {/* 24h-Course */}
                    <Col>24h-Course</Col>
                </Row>
            </Container >);
    }
}

export default App;
